mapper.update:
DECLARE
v_saleid NUMBER;

BEGIN
-- 판매기록 추가
INSERT INTO SALE (SALEID, SALEDATE, INCOME, MEMBERID, ACCOUNTID)
VALUES (SALEID_SEQ.nextval, SYSDATE, 0, #{memberID}, 'account')
RETURNING SALEID INTO v_saleid;


    -- 수입 누적
    UPDATE /*+ NO_PARALLEL(SALE) */ SALE
    SET INCOME = INCOME + #{item.price}
    WHERE SALEID = v_saleid;

    -- 상품 재고 감소
    UPDATE PRODUCT
    SET QUANTITY = QUANTITY - #{item.quantity}
    WHERE PRODUCTID = #{item.productID};

    -- 상품 감소에 따른 재료 감소
    UPDATE STOCK S
    SET S.QUANTITY = S.QUANTITY - (
    SELECT #{item.quantity} * PS.QUANTITY
    FROM PRODUCT_STOCK PS
    WHERE PS.STOCKID = S.STOCKID
    AND PS.PRODUCTID = #{item.productID}
    )
    WHERE S.STOCKID IN (
    SELECT PS.STOCKID
    FROM PRODUCT_STOCK PS
    );


-- 보유금 갱신
UPDATE /*+ NO_PARALLEL(BALANCE), NO_PARALLEL(SALE) */ BALANCE
SET ASSETS = ASSETS + (
SELECT INCOME
FROM (SELECT /*+ NO_PARALLEL(SALE) */ INCOME FROM SALE ORDER BY SALEID DESC)
WHERE ROWNUM = 1
),
LASTUPDATED = SYSDATE;

COMMIT;

-- 예외 처리
EXCEPTION
WHEN OTHERS THEN
ROLLBACK;
RAISE;
END;
