mapper.update:
UPDATE PRODUCT
SET PRODUCTNAME = #{productName},
    CATEGORY    = #{category},
    PRICE       = #{price}
WHERE PRODUCTID = #{productID}

UPDATE PRODUCTIMG
SET UUID     = #{uuid},
    ABSPATH  = #{absPath},
    RELPATH  = #{relPath},
    FILENAME = #{fileName}
WHERE PRODUCTID = #{productID}
