mapper.update:
UPDATE STOCK
SET QUANTITY = #{quantity},
    PRICE    = #{price}
WHERE STOCKID = #{stockID}

DECLARE
v_orderid NUMBER;

BEGIN
-- 발주기록 추가
INSERT /*+ NO_PARALLEL(STOCKORDER) */ INTO STOCKORDER (ORDERID, QUANTITY, EXPENSE, ASSETS, ORDERDATE, STOCKID,
                                                       EMPID, ACCOUNTID)
VALUES (ORDERID_SEQ.nextval, #{quantity}, #{price} * #{quantity}, 0, SYSDATE, #{stockID}, 1,
        'account') RETURNING ORDERID
INTO v_orderid;

-- 재료 개수 증가 및 보유 금액 감소
UPDATE /*+ NO_PARALLEL(STOCK) */ STOCK
SET QUANTITY = QUANTITY + #{quantity}
WHERE STOCKID = #{stockID};

-- 보유금 감소
UPDATE /*+ NO_PARALLEL(BALANCE) */ BALANCE
SET ASSETS = ASSETS - #{price} * #{quantity};

-- 발주 직후 보유 금액 기록
UPDATE /*+ NO_PARALLEL(STOCKORDER) */ STOCKORDER
SET ASSETS = (SELECT ASSETS FROM BALANCE)
WHERE ORDERID = v_orderid;

COMMIT;

-- 예외 처리
EXCEPTION
WHEN OTHERS THEN
ROLLBACK;
RAISE;
END;
